import React from 'react'
import api from "../utils/Api";

const Main = (props) => {

    const [userName, setUserName] = React.useState('');
    const [userDescription, setUserDescription] = React.useState('');
    const [userAvatar, setUserAvatar] = React.useState('');
    const [cards, setCards] = React.useState([]);

    React.useEffect(() => {
        api.getMyProfileInfo()
            .then((userData) => {
                setUserName(userData.name);
                setUserDescription(userData.avatar);
                setUserAvatar(userData.avatar)
            })
    }, [])

    React.useEffect(() => {
        api.getUsersCards()
            .then((userCards) => {
                setCards([...cards, userCards])
            })
    }, [])

    return (
        <main className="main">
            <section className="profile">
                <div className="profile__info-container">
                    <div className="profile__avatar-container">
                        <img src={userAvatar} alt="Картинка профиля" className="profile__avatar"/>
                        <button onClick={props.onEditAvatar} type="button" className="profile__avatar-button"/>
                    </div>
                    <div className="profile__info">
                        <div className="profile__info-name-container">
                            <h1 className="profile__info-name">{userName}</h1>
                            <button type="button" onClick={props.onEditProfile} className="profile__info-edit-button"/>
                        </div>
                        <p className="profile__info-rank">{userDescription}</p>
                    </div>
                </div>
                <button type="button" onClick={props.onAddPlace} className="profile__add-button"/>
            </section>
            <section className="places">
                {cards.map((card) => {
                    return (
                        <div className="place">
                            <img alt="Изображение места" className="place__image"/>
                            <div className="place__info">
                                <h2 className="place__name">{card.name}</h2>
                                <div className="place__fav-container">
                                    <button type="button" className="place__fav"/>
                                    <p className="place__fav-counter">{card.likes.length}</p>
                                </div>
                            </div>
                            <img src={card.link} alt="Кнопка удаления карточки"
                                 className="place__delete"/>
                        </div>
                    )
                })}
            </section>
        </main>
    )
}

export default Main
