const BASE_URL = `https://auth.nomoreparties.co`

export const register = (email, password) => {
    return fetch(`${BASE_URL}/signup`, {
        method: 'POST',
        headers: {
            'Content-type': 'application/json',
        },
        body: JSON.stringify({password, email})
    })
        .then((response) => {
            return response.json()}
        )
        .catch((err) => {
            console.log(err)
        })
}
