import PopupWithForm from "./PopupWithForm";
import React from "@types/react";
import {useRef, useState} from "react";

const EditAvatarPopup = ({isOpened, onClose}) => {
  const [link, setLink] = useState();
  const inputRef = useRef();

  return (
      <PopupWithForm
          name='edit'
          title='Редактировать аватар'
          onClose={onClose}
          isOpened={isOpened}>
          <>
              <label className="popup__field">
                  <input ref={inputRef} className="popup__input popup__input_type_title" name="link" type="url"
                         placeholder="Адрес картинки" id="link" defaultValue=""
                         required/>
                  <span className="popup__input-error link-input-error"/>
                  <input type="submit" className="popup__save popup__save_avatar-update" value="Сохранить"/>
              </label>
          </>
      </PopupWithForm>
  )
}

export default EditAvatarPopup
