import '../App.css';
import React from "react";
import '../index.css'
import api from '../utils/Api';
import { CurrentUserContext } from '../contexts/currentUserContext';
import {Switch, Route, withRouter, useHistory} from 'react-router-dom';
import PageContainer from './PageContainer';
import ProtectedRoute from './ProtectedRoute';
import Login from './Login';
import Register from "./Register";
import * as auth from "../utils/auth";
import Preloader from "./Preloader";

const App = () => {

    const [currentUser, setCurrentUser] = React.useState({});
    const [account, setAccount] = React.useState('')
    const [isLoggedIn, setIsLoggedIn] = React.useState(false);
    const [isLoading, setIsLoading] = React.useState(true);
    const history = useHistory()

    const [cards, setCards] = React.useState([]);

    const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = React.useState(false);
    const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = React.useState(false);
    const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = React.useState(false);
    const [selectedCard, setSelectedCard] = React.useState({
        isOpened: false,
        name: "",
        link: ""
    });

    const handleLogin = (email) => {
      setIsLoggedIn(true);
      setAccount(email);
    }

    const handleEditAvatarClick = () => {
        setIsEditAvatarPopupOpen(true);
    }

    const handleEditProfileClick = () => {
        setIsEditProfilePopupOpen(true);
    }

    const handleAddPlaceClick = () => {
        setIsAddPlacePopupOpen(true);
    }

    const closeAllPopups = () => {
        setIsAddPlacePopupOpen(false);
        setIsEditAvatarPopupOpen(false);
        setIsEditProfilePopupOpen(false);
        setSelectedCard({...selectedCard, isOpened: false})
    }

    const handleEscPress = (e) => {
      if (e.keyCode === 27) {
          closeAllPopups()
      }
    }

    const handleCardClick = (card) => {
        setSelectedCard({
            isOpened: true,
            name: card.name ,
            link: card.link,
        })
    }

    const handleUpdateUser = ({name, about}) => {
      api.updateUserInfo({name, about})
          .then(data => setCurrentUser({currentUser, ...data}))
          .catch(err => console.log(err))
      closeAllPopups();
    }

    const handleUpdateAvatar = (link) => {
        api.updateUserAvatar(link)
            .then(data => setCurrentUser({currentUser, ...data}))
            .catch(err => console.log(err))
        closeAllPopups();
    }

    const handlePlaceAdd = ({name, link}) => {
        api.addNewCard({name, link})
            .then(newCard => {
                setCards([newCard, ...cards])
            })
            .catch(err => console.log(err))
        closeAllPopups();
    }

    const handleCardLike = (card) => {
        const isLiked = card.likes.some(i => i._id === currentUser._id);
        debugger
        if (!isLiked) {
            api.addLike(card._id)
                .then((newCard) => {
                setCards((state) => state.map((c) => c._id === card._id ? newCard : c));
            })
                .catch(err => console.log(err));
        } else {
            api.removeLike(card._id)
                .then((newCard) => {
                    setCards((state) => state.map((c) => c._id === card._id ? newCard : c))})
                .catch(err => console.log(err))
        }

    }

    const handleCardDelete = (card) => {
        api.deleteCard(card._id)
            .then(() => {
                setCards((state) => state.filter((c) => c._id !== card._id))})
            .catch(err => console.log(err))
    }

    const handleTokenCheck = () => {
      if (localStorage.getItem('jwt')) {
          const jwt = localStorage.getItem('jwt');
          auth.checkToken(jwt)
              .then(({data})=> {
                  debugger;
                  console.log(data.email);
                  setAccount(data.email)
                  setIsLoggedIn(true);
                  history.push('/');
                  setIsLoading(false);
              })
              .catch(err => console.log(err))
              .finally(() => {
                  setIsLoading(false)
              })
      } else {
          setIsLoading(false)
      }
    }

    React.useEffect(() => {
        api.getMyProfileInfo()
            .then((userData) => {
                console.log(userData);
                setCurrentUser(userData)
            })
            .catch(err => console.log(err))
    }, [])

    React.useEffect(() => {
        api.getUsersCards()
            .then((userCards) => {
                setCards(userCards)
            })
            .catch(err => console.log(err))
    }, [])

    React.useEffect(() => {
        const handleEscPress = (e) => {
            if (e.key === 'Escape') {
                closeAllPopups();
            }
        }
        window.addEventListener('keydown', handleEscPress);
        return () => {
            window.removeEventListener('keydown', handleEscPress);
        }
    }, [closeAllPopups])

    React.useEffect(handleTokenCheck , [PageContainer])

  return (
      isLoading
          ? ( <Preloader/> )
          :
          ( <CurrentUserContext.Provider value={{currentUser, account, isLoggedIn}}>
                <Switch>
                    <ProtectedRoute
                        component={PageContainer}
                        exact
                        path={"/"}
                        handleEscPress={handleEscPress}
                        handleEditProfileClick={handleEditProfileClick}
                        handleEditAvatarClick={handleEditAvatarClick}
                        handleAddPlaceClick={handleAddPlaceClick}
                        handleCardClick={handleCardClick}
                        cards={cards}
                        user={currentUser}
                        handleUpdateUser={handleUpdateUser}
                        handleCardLike={handleCardLike}
                        handleCardDelete={handleCardDelete}
                        isAddPlacePopupOpen={isAddPlacePopupOpen}
                        closeAllPopups={closeAllPopups}
                        handlePlaceAdd={handlePlaceAdd}
                        isEditProfilePopupOpen={isEditProfilePopupOpen}
                        isEditAvatarPopupOpen={isEditAvatarPopupOpen}
                        handleUpdateAvatar={handleUpdateAvatar}
                        selectedCard={selectedCard}
                        loggedIn={isLoggedIn}
                    />
                    <Route path="/sign-in">
                        <Login onLogin={handleLogin}/>
                    </Route>
                    <Route path="/sign-up">
                        <Register/>
                    </Route>
                </Switch>
            </CurrentUserContext.Provider>))
};


export default withRouter(App);
